        val txt2 = getString(R.string.open)
        val txt3 = getString(R.string.close)
        val index = txt.toString().indexOf(txt2,ignoreCase = true)
        val indexStart1 = index+txt2.length
        val index2 = txt.toString().indexOf(txt3,ignoreCase = true)
        val indexStart2 = index2+txt3.length
        txt.setSpan(ForegroundColorSpan(ContextCompat.getColor(activity!!,R.color.colorGreen)),index,indexStart1, Spanned.SPAN_EXCLUSIVE_EXCLUSIVE)
        txtv_open_close_time.setText(txt, TextView.BufferType.SPANNABLE)
        txt.setSpan(ForegroundColorSpan(ContextCompat.getColor(activity!!,R.color.colorMaroon)),index2,indexStart2, Spanned.SPAN_EXCLUSIVE_EXCLUSIVE)
