package com.goddess.base.views.fragments


import android.text.SpannableString
import android.text.Spanned
import android.text.style.ForegroundColorSpan
import android.widget.TextView
import androidx.core.content.ContextCompat
import androidx.fragment.app.Fragment
import com.goddess.base.R
import com.goddess.base.viewmodels.BaseViewModel
import com.goddess.base.views.activities.BaseAppCompactActivity
import com.goddess.base.views.activities.doFragmentTransaction
import com.bumptech.glide.Glide
import kotlinx.android.synthetic.main.fragment_profile.*

/**
 * A simple [Fragment] subclass.
 */
class ProfileFragment : BaseFragment() {
    override val layoutId: Int
        get() = R.layout.fragment_profile
    override val viewModel: BaseViewModel?
        get() = null

    override fun init() {

        //Spannable String: to change the color of multiple texts in one textView
        val txt = SpannableString(getString(R.string.punta_cana))
        val txt2 = getString(R.string.dominican_Republic)
        val index = txt.toString().indexOf(txt2, ignoreCase = true)
        val indexend = index + txt2.length
        txt.setSpan(ForegroundColorSpan(ContextCompat.getColor(activity!!, R.color.colorGray3)), index, indexend, Spanned.SPAN_EXCLUSIVE_EXCLUSIVE)
        txTvpunta.setText(txt, TextView.BufferType.SPANNABLE)

        val txt3 = SpannableString(getString(R.string.punta_cana))
        val txt4 = getString(R.string.dominican_Republic)
        val index2 = txt3.toString().indexOf(txt4, ignoreCase = true)
        val indexend2 = index2 + txt4.length
        txt.setSpan(ForegroundColorSpan(ContextCompat.getColor(activity!!, R.color.colorGray3)), index, indexend2, Spanned.SPAN_EXCLUSIVE_EXCLUSIVE)
        txTvPunta2.setText(txt, TextView.BufferType.SPANNABLE)
        Glide.with(this)
                .load("http://destination-montenegro.com/wp-content/uploads/2017/08/budva-wp-srednji.jpg")
                .placeholder(R.drawable.ic_profile)
                .into(iv_image2)
        Glide.with(this)
                .load("data:image/jpeg;base64,")
                .placeholder(R.drawable.ic_profile)
                .into(iv_image3)
        Glide.with(this)
                .load("data:image/jpeg;base64,")
                .placeholder(R.drawable.ic_profile)
                .into(iv_image4)
        Glide.with(this)
                .load("data:image/jpeg;base64,")
                .placeholder(R.drawable.ic_profile)
                .into(iv_image5)
        Glide.with(this)
                .load("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTn0nWvyA6vS94nVKCIiHdxdWWJ6FRdJsVroTjOfCZhixzKcp2m&s")
                .placeholder(R.drawable.ic_profile)
                .into(iv_image6)
        Glide.with(this)
                .load("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSgj_QqHE_CrBf5NBxSC2kTlI_dpMyXctQkxX7C-qi1NzUtBvXO&s")
                .placeholder(R.drawable.ic_profile)
                .into(iv_image7)
        Glide.with(this)
                .load("https://www.gladeculturalcenter.com/images/teachers/original/Alex_Profile.jpg")
                .placeholder(R.drawable.ic_profile)
                .into(iv_circleimage)
        ivToolbarActionEnd.setImageResource(R.drawable.ic_settings)
        ivToolbarActionEnd.visibility = (view!!.visibility)
        ivToolbarActionEnd.setOnClickListener {  (activity as BaseAppCompactActivity).doFragmentTransaction(fragManager = activity!!.supportFragmentManager, containerViewId = R.id.flFragContainerMain, fragment = SettingsFragment(), isAddFragment = false) }

    }


